import React, { useEffect, useState } from "react";
import NewTask from "./components/NewTask/NewTask";
import Tasks from "./components/Tasks/Tasks";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);

  const fetchTasks = async (taskText) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://react-http-request-f984e-default-rtdb.firebaseio.com/tasks.json");
      if (!response.ok) {
        throw new Error('Request Failed!')
      }

      const data = await response.json();

      const loadedTasks = [];

      for (const key in data) {
        loadedTasks.push({ id: key, text: data[key].text });
      }

      setTasks(loadedTasks);
    } catch (err) {
      setError(err.message || 'Something went wrong!')
    }
    setIsLoading(false);
  }

  useEffect(() => {
    fetchTasks();
  }, [])

  const taskAddHandler = (task) => {
    setTasks((prevTask) => prevTask.concat(task));
  }


  return (
    <React.Fragment>
      <NewTask onAddTask={taskAddHandler}></NewTask>
      <Tasks
        items={tasks}
        loading={isLoading}
        error={error}
        onFetch={fetchTasks}
      />
    </React.Fragment>
  )
}

export default App;
